import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// core components
import Card from "../../../components/shared/Card/Card.js";
import CardHeader from "../../../components/shared/Card/CardHeader.js";
import CardBody from "../../../components/shared/Card/CardBody.js";

import styles from "../../../assets/jss/material-dashboard-react/views/iconsStyle.js";
const useStyles = makeStyles(styles);

// optional cofiguration
const alertOptions = {
    // you can also just use 'bottom center'
    position: positions.BOTTOM_LEFT,
    timeout: 5000,
    offset: '30px',
    // you can also just use 'scale'
    transition: transitions.SCALE
  }

export default function Alert({ style, options, message, close }) {
  const classes = useStyles();
  return (
    <Card plain style={{margin: '15px'}}>
        <CardHeader plain color='primary'>
            <div>
                <label style={{color:'#fff'}}>Browser</label>
                <div style={{float:'right', textAlign:'right', verticalAlign: 'top', width: '50px'}}>
                    <button onClick={close}>X</button>
                </div>
            </div>
        </CardHeader>
        <CardBody style={{backgroundColor: '#fff'}}>
            <div style={style} color="primary">
                {options.type === 'info' && '!'}
                {options.type === 'success' && ':)'}
                {options.type === 'error' && ':('}
                {message}
                {JSON.stringify(options)}
            </div>
        </CardBody>
    </Card>
  );
}
