import axios from 'axios';

export function ListAllBookmarks(user) {
    console.log('in bookmark' + user);
    const request =  axios.get(
        //TODO url must be from configuration
        'http://localhost:2000/bookmark/' + user,
      ).then(result =>  {
          console.log('boomark action');
          var mData = result.data.map(d => {
              var dItem = {
                'Icon' : "folder",
                'PATH' : d.path
              };
              return dItem;
            });

            console.log(mData);

            return mData;
      });

    return {
        type: 'LIST_BOOKMARK',
        payload: request
    }
}

export function AddBookmark(user, folder) {

    const request =  axios.post(
        //TODO url must be from configuration
        'http://localhost:2000/bookmark/add', { user, folder }
      ).then(result =>  {
          console.log(result);
          return result;
      });

    return {
        type: 'ADD_BOOKMARK',
        payload: request
    }
}

export function ClearState() {
    return {
        type: 'CLEAR_STATE',
        payload: null
    }
}
