import axios from 'axios';

export function ListAllBookmark(user, type) {
    console.log('in bookmark');
    const request =  axios.get(
        //TODO url must be from configuration
        'http://localhost:2000/bookmark' + user,
      ).then(result =>  {
          console.log(result.data);
          var mData = result.data.map(d => {
              var dItem = {
                'Icon' : "folder",
                'PATH' : d.path
              };
              return dItem;
            });

            console.log(mData);

            return mData;
      });

    return {
        type: 'LIST_RECENT',
        payload: request
    }
}

export function AddBookmark(user, bookmark) {
    const request =  axios.post(
        //TODO url must be from configuration
        'http://localhost:2000/bookmark' + user, bookmark
      ).then(result =>  {
          console.log(result);
          return result;
      });

    return {
        type: 'LIST_RECENT',
        payload: request
    }
}

export function ClearState() {
    return {
        type: 'CLEAR_STATE',
        payload: null
    }
}
