import axios from 'axios';

export function ListAllRecent(user) {
    const request =  axios.get(
        //TODO url must be from configuration
        'http://localhost:2000/recent/' + user,
      ).then(result =>  {
          console.log('ListAllRecent' + user);
          var mData = result.data.map(d => {
              var dItem = {
                'Icon' : "folder",
                'PATH' : d.Path,
                'DisplayName': d.PATH
              };
              return dItem;
            });

            return mData;
      });

    return {
        type: 'LIST_Recent',
        payload: request
    }
}

export function ClearState() {
    return {
        type: 'CLEAR_STATE',
        payload: null
    }
}
